INSERT INTO [dbo].[Glossaire] ([Appl], [Code], [Libelle], [Tooltip]) VALUES (N'VRB', 1, N'Vraisemblance négligeable', N'Il ne semble pas possible que les sources de risques retenues puissent réaliser la menace en s’appuyant sur les caractéristiques des supports (ex. : vol de supports papiers stockés dans un local de l’organisme dont l’accès est contrôlé par badge et code d’accès).')
INSERT INTO [dbo].[Glossaire] ([Appl], [Code], [Libelle], [Tooltip]) VALUES (N'VRB', 2, N'Vraisemblance limitée', N'Il semble difficile pour les sources de risques retenues de réaliser la menace en s’appuyant sur les caractéristiques des supports (ex. : vol de supports papiers stockés dans un local de l’organisme dont l’accès est contrôlé par badge).')
INSERT INTO [dbo].[Glossaire] ([Appl], [Code], [Libelle], [Tooltip]) VALUES (N'VRB', 3, N'Vraisemblance importante', N'il semble possible pour les sources de risques retenues de réaliser la menace en s’appuyant sur les caractéristiques des supports (ex. : vol de supports papiers stockés dans les bureaux d’un organisme dont l’accès est contrôlé par une personne à l’accueil).')
INSERT INTO [dbo].[Glossaire] ([Appl], [Code], [Libelle], [Tooltip]) VALUES (N'VRB', 4, N'Vraisemblance maximale', N'il semble extrêmement facile pour les sources de risques retenues de réaliser la menace en s’appuyant sur les caractéristiques des supports (ex. : vol de supports papier stockés dans le hall public de l’organisme).')
